import React from 'react';
import './Card.css';
import {Link} from 'react-router-dom';

const Card = (props) => {
    return(
        <div className="card promoting-card mt-2" style={{height: "600px"}}>
            <div className="view overlay">
                <img className="card-img-top" src={props.url} alt="Recipe" style={{height: "300px"}} />
            </div>
            <div className="card-body">
                <h5 className="card-title font-weight-bold">{props.name} <h6>â€¢{props.category}</h6></h5>
                <p className="card-text">{props.description}</p>
                <Link className="btn btn-primary" to="/">Checkout</Link>
            </div>
        </div>
            
    )
}

export default Card;
