import React from 'react';
import './Card.css';
import {Link} from 'react-router-dom';

const Card = (props) => {
    return(
        <div className="card promoting-card mt-2" style={{height: "600px"}}>
            <div className="view overlay">
                <img className="card-img-top" src={props.url} alt="Recipe" style={{height: "300px"}} />
            </div>
            <div className="card-body">
                <div class="card-image-overlay">
                    <span class="card-detail-badge m-1">{props.category}</span>
                    <span class="card-detail-badge m-1">{props.taste}</span>
                </div>
                <h5 className="card-title font-weight-bold mt-2">{props.name.toUpperCase()}</h5>
                <p className="card-text">{props.description}</p>
            </div>
                <div className="card-footer">
                    <Link className="btn btn-primary " to="/">Checkout</Link>
                </div>
        </div>
            
    )
}

export default Card;
